n.ProjectId=PD.ProjectCode left join  tbl_template tmp on PD.Template=tmp.id where  tmp.Template_id in   (2,4) union select  ML_Pln.* from ( select aa.*,bb.SubTaskName from (select ProjectId , StartDt ,EndDt  ,SubTask_Id ,  sum(time_to_sec(Planned_Hrs)) Planned_Hrs , User from  tbl_sub_task_plan_dtl  where is_active =1  Group by ProjectId, User , StartDt ,EndDt  ,SubTask_Id)aa Left Join   tbl_sub_task_dtl bb on aa.SubTask_Id=bb.Id )ML_Pln left join  tbl_projects PD on ML_Pln.ProjectId=PD.ProjectCode left join  tbl_template tmp on PD.Template=tmp.id where  tmp.Template_id in   (3,5) )aa Left Join (select  time_to_sec(ts.Hrs) Actual_Hrs ,  ts.User ,PD.ProjectCode,case when tmp.Template_id in   (3,5) then SubTaskId when  tmp.Template_id in   (2,4)  then TaskId else MilestoneId end MID  from  tbl_timesheet ts left join  tbl_projects PD on ts.ProjectId=PD.ProjectId left join  tbl_template tmp on PD.Template=tmp.id where ts.is_approval<> 0 Group by  PD.ProjectCode ,  ts.User,case when tmp.Template_id in   (3,5) then SubTaskId when  tmp.Template_id in   (2,4)  then TaskId else MilestoneId end )bb on aa.ProjectId=ProjectCode and aa.M_Id=bb.MID and aa.User=bb.User )qr Left Join (select ProjectCode , ifnull(ifnull(Sub_Task_Id, Task_Id ) ,Milestone_Id) Mid ,  User, round(avg(Score) ,2)Score   from  tbl_quality_score_dtl where is_active=1 Group by ProjectCode , ifnull(ifnull(Sub_Task_Id, Task_Id ) ,Milestone_Id)   , User  ) QC on qr.ProjectId=qc.ProjectCode and qr.M_Id=qc.Mid and qr.User=qc.User )v2 on v1.ProjectCode=v2.ProjectId where v2.ProjectId is not null Group by v2.ProjectId,   v1.ProjectName,v1.ClientId  ,v1.ClientName;
