CREATE DEFINER=`align_Global`@`%` PROCEDURE `Usp_ClientWise_Dtl`(Frdt Date ,Tdt Date , Flg int,In_clientID text)
BEGIN

 
declare i int;
declare l int;
set @frdt =Frdt ;
set @todt =Tdt ;
Set @flag =Flg ; 
drop table if exists tbl_client_id ;
 create table tbl_client_id(clientsId int);
 set l=length(In_clientID)-LENGTH(REPLACE(In_clientID, ',', '')) ;
 set i=1;
while i<=l 
do
insert into tbl_client_id(clientsId)
select split(In_clientID,',',i);
set i=i+1;
end while;

 


 set @W_days=(select  count(*) Days from ( 
 select * from  (
   SELECT ADDDATE( @frdt, INTERVAL @i:=@i+1 DAY) AS DAY
    FROM ( SELECT a.a
        FROM (SELECT 0 AS a UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9) AS a
        CROSS JOIN (SELECT 0 AS a UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9) AS b
        CROSS JOIN (SELECT 0 AS a UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9) AS c
        
                CROSS JOIN (SELECT 0 AS a UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9) AS d
    ) a
    JOIN (SELECT @i := -1) r1     WHERE      @i < DATEDIFF( @todt ,  @frdt) 
)dateTable WHERE WEEKDAY(dateTable.Day) not IN (5,6)  
)aa);

 
Drop table if exists Temp_rescouce_Dtl;
Create Table Temp_rescouce_Dtl(
Resources varchar(255), User int, Booked_Hrs time, Booked_Hrs_Sec int, Billable time , Billable_Sec int, Non_Billable time, Non_Billable_Sec int, SickLeave time, Sick_leave_Sec int, Percentage int, Days int, Location varchar(255), ProjectCode  varchar(50),ProjectName varchar(255), PerHrRate decimal(50,2) ,
BillableValue decimal(50,2),ClientName varchar(100),ClientId int , InvoicedSec  int,
Invoiced_Hrs Time , PendingBillableHrs Time
 );

Insert into Temp_rescouce_Dtl
 select 
Resources, aa.User, Booked_Hrs, Booked_Hrs_Sec, Billable, Billable_Sec, Non_Billable, Non_Billable_Sec, SickLeave, Sick_leave_Sec, Percentage, ifnull(@W_days,0)Days  ,  aa.Location ,ProjectCode,ProjectName , PerHrRate ,( ( (Billable_Sec+  Non_Billable_Sec)   / 60 )  / 60   ) * PerHrRate BillableValue, ClientName,ClientId,
InvoicedSec , sec_to_Time(InvoicedSec) ,
sec_to_Time(Billable_Sec  - InvoicedSec) PendingBillableHrs

from (SELECT   name Resources , a.User , cast(SEC_TO_TIME(sum(TIME_TO_SEC(Hrs))) as time) Booked_Hrs ,
sum(TIME_TO_SEC(Hrs)) Booked_Hrs_Sec ,   
cast(SEC_TO_TIME(sum(TIME_TO_SEC(case when BillingStatus=1 then  Hrs else 0  end)))  as time) Billable , 
round(sum(TIME_TO_SEC(case when BillingStatus=1 then  Hrs else 0  end)) ,0)  Billable_Sec , 
cast(SEC_TO_TIME(sum(TIME_TO_SEC(case when BillingStatus=2 then  Hrs  else 0  end))) as time)  Non_Billable,
round(sum(TIME_TO_SEC(case when BillingStatus=2 then  Hrs else 0  end)) ,0)  Non_Billable_Sec ,
cast(SEC_TO_TIME(sum(TIME_TO_SEC(case when b.TaskName like '%Sick leave%' then  Hrs  else 0  end))) as time)  SickLeave,
round(sum(TIME_TO_SEC(case when  b.TaskName like '%Sick leave%' then  Hrs else 0  end)) ,0)  Sick_leave_Sec ,
avg(ifnull(Percentage,0)) Percentage , Loc.Location ,d.ProjectCode,d.ProjectName , avg(ifnull(PerHrRate,0))PerHrRate,c.ClientName,c.ClientId ,

sum(case when IsInvoiced=1 and BillingStatus=1 then TIME_TO_SEC(Hrs) else  0  end) InvoicedSec


 FROM (select * from tbl_timesheet where 1=(case when In_clientID=',' then 1 else ClientId in(select clientsId  from tbl_client_id)  end)) a 
left Join tbl_task_dtl b on a.taskid=b.id
left Join client_mstr c on a.ClientId=c.ClientId
left Join tbl_projects d on a.ProjectId=d.ProjectId
left join users e on a.user=e.id 
left join  (
select  a.*, b.Location from trn_user_location a Left Join   location_mstr b on a.LocationId=b.Id where a.is_deleted=0 )loc on a.EntryDate between 
loc.EffectiveFdt and loc.EffectiveTdt and a.User=loc.Userid

where a.EntryDate between @frdt and @todt and a.is_deleted<>1  
Group by name,Loc.Location ,ClientId   ) aa  
 Order by 1;
  
  
  
  

if @flag=1 then
  
  select * from (
  select aa.* ,bb.ProjectValue   from (
  select * from (
 select ProjectCode ,ProjectName,ClientName,ClientId, sum(BillableValue)  BillableValue , SEC_TO_TIME(sum(Booked_Hrs_Sec)) Booked_Hrs ,SEC_TO_TIME(sum(Non_Billable_Sec)) Non_Billable_Hrs ,   SEC_TO_TIME(sum(Billable_Sec) ) Billable_Hrs,   SEC_TO_TIME(sum(InvoicedSec) ) InvoicedSec_Hrs, sec_to_Time(sum(Billable_Sec  - InvoicedSec)) PendingBillableHrs   from Temp_rescouce_Dtl Group by ProjectCode
 
