CREATE DEFINER=`align_Global`@`%` PROCEDURE `Usp_Expense_Leger_Report`(I_Ft Date ,I_Todt Date,i_client Text )
BEGIN
set @frdt:= I_Ft;
set @todt:= I_Todt;

set @clientid=i_client;

drop table if exists temp_Leg_Ct_Filter;
Create table temp_Leg_Ct_Filter 

select 
substring_index( substring_index( @clientid, ',', n), ',', -1 ) Ct_fltr 
from number where char_length( @clientid) - char_length(replace( @clientid, ',', '')) >= n - 1 ;

drop table if exists legder_temp_dt;
 Create   table legder_temp_dt( id  int auto_increment , MinDt date ,  MaxDt date  , Mnth varchar(50) , Yr varchar(10)  ,primary key(id) ) ; 
 
drop table if exists legder_temp_view;
 Create   table legder_temp_view( 
Projectcode varchar(255), Project varchar(255),VoucherNo varchar(255),  Dateofvisit Date , Description text ,name_of_person  varchar(255) , amount decimal(20,2), `OPE_Charged_Against_Invoice` decimal(20,2), Isinvoiced varchar(25), InoviceNO text, clientid int , ClientName  varchar(255) , flag  int ,Mindt Date,Maxdt Date , Ridn  int ,Partner text, TeamLead Text );

 
Insert into legder_temp_dt(MinDt , Maxdt , Mnth , Yr  ) 
 select min(Dt) MinDt,max(Dt)Mxdt , Monthname(Dt)Mnth,Year(dt)Yr from  (
   SELECT ADDDATE( @frdt, INTERVAL @i:=@i+1 DAY) AS Dt
    FROM ( SELECT a.a
        FROM (SELECT 0 AS a UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9) AS a
        CROSS JOIN (SELECT 0 AS a UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9) AS b
        CROSS JOIN (SELECT 0 AS a UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9) AS c
        
                CROSS JOIN (SELECT 0 AS a UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9) AS d
    ) a
    JOIN (SELECT @i := -1) r1     WHERE      @i < DATEDIFF(@todt,  @frdt) 
)dateTable Group by Monthname(Dt) ,Year(dt) Order by 1  ;

set @iMin:=(select min(id) from legder_temp_dt);
	set @imax=(select max(id) from legder_temp_dt);

while @iMin<=@imax do
	if @iMin<>0 
    then 
    
    set @mindt := (select MinDt from legder_temp_dt where id=@iMin)  ;
    set @maxt := (select MaxDt from legder_temp_dt where id=@iMin)  ;
    -- select  @mindt  ,  @maxt  ;
    
    -- Trans Data begin    
   Insert into legder_temp_view (Projectcode, Project, VoucherNo , Dateofvisit,Description ,  name_of_person, amount, `OPE_Charged_Against_Invoice`, Isinvoiced, InoviceNO, clientid, ClientName, flag, Mindt, Maxdt, Ridn)
    
    select e.Project,concat(p.ProjectName,' | ',cat.Category)Project,e.VoucherNo ,e.dt Dateofvisit,e.Description, u.name name_of_person,e.amount, case when  e.Isinvoiced='Y' then ifnull(e.amount,0) else 0 end  'OPE_Charged_Against_Invoice' , e.Isinvoiced,e. InoviceNO,p.clientid,c.ClientName,2 flag , @mindt ,  @maxt , @iMin   from tbl_add_expense e left join tbl_projects p on p.Projectcode=e.Project left join users u on u.id=e.created_by_id left join client_mstr c on c.ClientId=p.ClientId left join tbl_project_category_mstr cat on cat.id=p.ProjectCatId where e.dt between @mindt and @maxt and 1=case when @clientid<>'' then p.clientid in(select Ct_fltr from temp_Leg_Ct_Filter) else 1 end   
      and ifnull(e.CreditNote_Exp_Status,'')<>'Approved'  /*creditnote ignore*/

    union all
    
SELECT a.ProjectCode  , concat(b.ProjectName,' | ',cat.Category)   ProjectName  ,'',a.InvoiceDate ,null ,  null name_of_person , 0 amount , a.ExpenseAmt+round((ifnull(a.BasicAmount,0)*ifnull(a.Expense_Percentage,0))/100) ExpenseAmt , 'Y' Isinvoiced , a.InvoiceNo   ,  b.ClientId , c.ClientName ,2 flag , @mindt ,  @maxt , @iMin    FROM tbl_invoice_dtl a  left join tbl_projects b on a.Projectcode=b.Projectcode  left join client_mstr c on c.ClientId=b.ClientId
left join tbl_project_category_mstr cat on cat.id=b.ProjectCatId 
  
where  a.InvoiceDate between @mindt and @maxt  
and ifnull(a.Status,'')='Approved' and 1=case when @clientid<>'' then b.clientid in(select Ct_fltr from temp_Leg_Ct_Filter) else 1 end  


union all
-- Creditnote Begin
