 Left Join tbl_Projects p on b.Projectcode=p.Projectcode
 left join client_mstr c on c.ClientId=p.ClientId left join tbl_project_category_mstr cat on cat.id=p.ProjectCatId   where   ifnull(b.Status,'')='Approved' and 1=case when @clientid<>'' then b.clientid in(select Ct_fltr from temp_Leg_Ct_Filter) else 1 end   ;
 -- Creditnote end
