      and ifnull(e.CreditNote_Exp_Status,'')<>'Approved'  /*creditnote ignore*/
      
      union all
      
      select a.Projectcode,concat(a.ProjectName,' | ',cat.Category),null,null ,sum(Opening_Bal) , 0 , '','', a.ClientId , b.ClientName,4 flag , @mindt ,  @maxt , @iMin  from Tbl_projects a left join client_mstr  b on a.ClientId=b.ClientId 
