where a.Project_approval='Approved' and Is_Grp=0 and Op_Dt  <= @mindt and 1=case when @clientid<>'' then a.clientid in(select Ct_fltr from temp_Leg_Ct_Filter) else 1 end Group by a.ClientId , b.ClientName ,a.Projectcode,concat(a.ProjectName,' | ',cat.Category)
      
      
      
    union all
    
SELECT a.ProjectCode  , concat(b.ProjectName,' | ',cat.Category)   ProjectName  ,a.InvoiceDate ,  null name_of_person , 0 amount , a.ExpenseAmt+round((ifnull(a.BasicAmount,0)*ifnull(a.Expense_Percentage,0))/100) ExpenseAmt , 'Y' Isinvoiced , a.InvoiceNo   ,  b.ClientId , c.ClientName ,4 flag , @mindt ,  @maxt , @iMin    FROM tbl_invoice_dtl a  left join tbl_projects b on a.Projectcode=b.Projectcode  left join client_mstr c on c.ClientId=b.ClientId
left join tbl_project_category_mstr cat on cat.id=b.ProjectCatId 
where  a.InvoiceDate  <= @maxt and ifnull(a.Status,'')='Approved' and 1=case when @clientid<>'' then b.clientid in(select Ct_fltr from temp_Leg_Ct_Filter) else 1 end  



union all
-- Creditnote Begin
select  p.Projectcode,concat(p.ProjectName,' | ',cat.Category)Project,CreditNoteDte Dateofvisit,  null name_of_person,0 AdhocExpAmt, - a.AdhocExpAmt  'OPE_Charged_Against_Invoice' , 'Y' Isinvoiced, CreditNote_No,p.clientid,c.ClientName,4 flag , @mindt ,  @maxt , @iMin 
 from (
 select  sum(ExpenseAmt+Expense_Percentage_Value) AdhocExpAmt  , RefInvid,CreditNote_No,CreditNoteDte from tbl_invoice_creditnote where  ifnull(Status,'')='Approved' and  CreditNoteDte  <= @maxt   Group by RefInvid,CreditNote_No,CreditNoteDte)a
 left Join tbl_Invoice_dtl b on a.RefInvid=b.id 
 Left Join tbl_Projects p on b.Projectcode=p.Projectcode
 left join client_mstr c on c.ClientId=p.ClientId left join tbl_project_category_mstr cat on cat.id=p.ProjectCatId   where 1=case when @clientid<>'' then b.clientid in(select Ct_fltr from temp_Leg_Ct_Filter) else 1 end   and ifnull(b.Status,'')='Approved'  
 -- Creditnote end


 )obs   Group by  clientid,ClientName, Project   ,ProjectName
 ;
 -- Closing Data end 

 
-- Opening Data begin
 
   Insert into legder_temp_view (Projectcode, Project, Description ,  amount,  clientid, ClientName, flag, Mindt, Maxdt, Ridn)

   
