          and ifnull(e.CreditNote_Exp_Status,'')<>'Approved'  /*creditnote ignore*/

    union all
    
SELECT a.ProjectCode  , concat(b.ProjectName,' | ',cat.Category)   ProjectName  ,a.InvoiceDate ,  null name_of_person , 0 amount , a.ExpenseAmt+round((ifnull(a.BasicAmount,0)*ifnull(a.Expense_Percentage,0))/100) ExpenseAmt , 'Y' Isinvoiced , a.InvoiceNo   ,  b.ClientId , c.ClientName ,1 flag , @mindt ,  @maxt , @iMin    FROM tbl_invoice_dtl a  left join tbl_projects b on a.Projectcode=b.Projectcode  left join client_mstr c on c.ClientId=b.ClientId
left join tbl_project_category_mstr cat on cat.id=b.ProjectCatId 
where  a.InvoiceDate  < @mindt and 1=case when @clientid<>'' then b.clientid in(select Ct_fltr from temp_Leg_Ct_Filter) else 1 end  and ifnull(a.Status,'')='Approved'

union all

select a.Projectcode,concat(a.ProjectName,' | ',cat.Category),null,null ,sum(Opening_Bal) , 0 , '','', a.ClientId , b.ClientName,1 flag , @mindt ,  @maxt , @iMin  from Tbl_projects a left join client_mstr  b on a.ClientId=b.ClientId 
left join tbl_project_category_mstr cat on cat.id=a.ProjectCatId
where  a.Project_approval='Approved' and  Is_Grp=0 and Op_Dt  <= @mindt and 1=case when @clientid<>'' then a.clientid in(select Ct_fltr from temp_Leg_Ct_Filter) else 1 end Group by a.ClientId , b.ClientName ,a.Projectcode,concat(a.ProjectName,' | ',cat.Category)

