update tbl_projects a left Join tbl_project_category_mstr b on a.ProjectCatId=b.Id
set a.HSN_SAC =b.HSN_SAC where  a.ProjectCode=@Pcd ;
 
 
select ProjectId, ProjectCode  from tbl_projects where ProjectCode=@Pcd;
 
 
 
