CREATE DEFINER=`align_Global`@`%` PROCEDURE `Usp_timesheet_trigger_dtl`()
BEGIN

set @frdt=   cast(DATE_ADD(now() , INTERVAL -21 DAY) as Date);
set @todt=   cast(now() as Date);

set @Stday=(select WeekStart from tbl_timesheet_conditions) ;  -- day of week -- this important
set @Act_Stday=  @Stday- 1 ; 


Drop table if EXISTS temp_TS_trigger_Date;

Drop table if EXISTS temp_dt_vw;
 create    table temp_dt_vw


select * ,week(Dte) Wk , DAYNAME(Dte)Day,WEEKDAY(Dte)wkday ,

CASE WHEN (weekday(Dte)<=@Act_Stday) THEN date(Dte + INTERVAL (@Act_Stday-weekday(Dte)) DAY)
    ELSE date(Dte + INTERVAL (@Act_Stday+7-weekday(Dte)) DAY)
   END as week_days


 from (
SELECT ADDDATE( @frdt, INTERVAL @i:=@i+1 DAY) AS Dte
FROM ( SELECT a.a
FROM (SELECT 0 AS a UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9) AS a
CROSS JOIN (SELECT 0 AS a UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9) AS b
CROSS JOIN (SELECT 0 AS a UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9) AS c

CROSS JOIN (SELECT 0 AS a UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9) AS d
) a
JOIN (SELECT @i := -1) r1 WHERE @i < DATEDIFF(@todt, @frdt)
)dateTable  

Order by  (Dte) ;

 
 set @mxdt:=(select max(week_days) from temp_dt_vw) ; 
 Create table temp_TS_trigger_Date
select * from temp_dt_vw    where week_days = DATE_ADD(@mxdt , INTERVAL -7 DAY) order by Dte  ;

Alter table temp_TS_trigger_Date Add id int NOT NULL AUTO_INCREMENT   PRIMARY KEY  ;
-- Drop table temp_dty ; 

END ;;
