BEGIN


set @frdt =Frdt ;
set @todt =Todt;
 
 -- Working Day end
 
Drop table if exists  Temp_Working_Days ; 
Create Table Temp_Working_Days(Days int ,User int , Location Varchar(255));
Insert into  Temp_Working_Days
 
 
select  count(*) Days , aaa.User ,aaa.Location  from ( 
 select * from ( 
 select * from  (
   SELECT ADDDATE( @frdt, INTERVAL @i:=@i+1 DAY) AS DAY
    FROM ( SELECT a.a
        FROM (SELECT 0 AS a UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9) AS a
        CROSS JOIN (SELECT 0 AS a UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9) AS b
        CROSS JOIN (SELECT 0 AS a UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9) AS c
    ) a 
    JOIN (SELECT @i := -1) r1     WHERE      @i < DATEDIFF(@todt,  @frdt) 
)dateTable WHERE WEEKDAY(dateTable.Day) not IN (5,6)  
)aa
Left Join (


 select Distinct  User,EffectiveFdt , EffectiveTdt , Location  from (select * from tbl_timesheet where EntryDate between @frdt and @todt and is_deleted<>1 )a left join  ( select  a.*, b.Location from trn_user_location a Left Join   location_mstr b on a.LocationId=b.Id where a.is_deleted=0 )loc on a.EntryDate between  loc.EffectiveFdt and loc.EffectiveTdt and a.User=loc.Userid)bb on aa.Day between bb.EffectiveFdt and bb.EffectiveTdt

)aaa Left Join (
select Distinct Location , Date , Description  from holiday_mstr)bbb on aaa.Location=bbb.Location and aaa.Day=bbb.date
where bbb.Location is   null Group by aaa.Location,aaa.User;

-- Working Day end


select aa.* ,bb.PerHrRate , 

ifnull(Billable_Hrs_per,0) + ifnull(Non_Billable_Hrs_per,0) Ut_Per
  from (

select 
Resources, aa.User, Booked_Hrs, Booked_Hrs_Sec, Billable, Billable_Sec, Non_Billable, Non_Billable_Sec, SickLeave, Sick_leave_Sec,Vacation,Vacation_sec, Percentage, ifnull(Days,0)Days , ifnull(Avail_Hrs_Sec,0)Actual_Avail_Hrs_Sec   

, (ifnull(Avail_Hrs_Sec,0)* Percentage ) / 100  Avail_Hrs_Sec 


 , SEC_TO_TIME((ifnull(Avail_Hrs_Sec,0)*Percentage)/100) Avail_Hrs , 
SEC_TO_TIME(ifnull(Avail_Hrs_Sec,0)) Actual_Avail_Hrs , Days ActualWorkingDays
 ,


ifnull((Booked_Hrs_Sec  / ifnull((Avail_Hrs_Sec*Percentage)/100 ,0) )*100,0) Utilization_Per  ,sec_to_time(ifnull( (ifnull(Avail_Hrs_Sec,0)* Percentage ) / 100,0)-ifnull(Booked_Hrs_sec,0)) Difference,   


((( (ifnull(Billable_Sec,0)) / 60 ) / 60 ) / nullif((( (ifnull(Avail_Hrs_Sec,0)) / 60 ) / 60 ) ,0))*100  Billable_Hrs_per,

((((ifnull( Non_Billable_Sec ,0)+ ifnull( Sick_leave_Sec ,0) + ifnull(Vacation_sec,0) )/60) /60)
/((ifnull(Avail_Hrs_Sec ,0)/60/60))*100) Non_Billable_Hrs_per,aa.Location
 from (SELECT   name Resources , a.User , cast(SEC_TO_TIME(sum(TIME_TO_SEC(Hrs))) as time) Booked_Hrs ,
sum(TIME_TO_SEC(Hrs)) Booked_Hrs_Sec ,   
cast(SEC_TO_TIME(sum(TIME_TO_SEC(case when BillingStatus=1 then  Hrs else 0  end)))  as time) Billable , 
round(sum(TIME_TO_SEC(case when BillingStatus=1 then  Hrs else 0  end)) ,0)  Billable_Sec , 
cast(SEC_TO_TIME(sum(TIME_TO_SEC(case when BillingStatus=2 then  Hrs  else 0  end))) as time)  Non_Billable,
round(sum(TIME_TO_SEC(case when BillingStatus=2 then  Hrs else 0  end)) ,0)  Non_Billable_Sec ,
cast(SEC_TO_TIME(sum(TIME_TO_SEC(case when b.TaskName like '%Sick leave%' then  Hrs  else 0  end))) as time)  SickLeave,
round(sum(TIME_TO_SEC(case when  b.TaskName like '%Sick leave%' then  Hrs else 0  end)) ,0)  Sick_leave_Sec ,
cast(SEC_TO_TIME(sum(TIME_TO_SEC(case when b.TaskName like '%Vacation%' then  Hrs  else 0  end))) as time)  Vacation,
round(sum(TIME_TO_SEC(case when  b.TaskName like '%Vacation%' then  Hrs else 0  end)) ,0)  Vacation_Sec,
avg(ifnull(Percentage,0)) Percentage , Loc.Location
 FROM tbl_timesheet a 
left Join tbl_task_dtl b on a.taskid=b.id
left Join client_mstr c on a.ClientId=c.ClientId
left Join tbl_projects d on a.ProjectId=d.ProjectId
left join users e on a.user=e.id 

left join  (
