loc.EffectiveFdt and loc.EffectiveTdt and a.User=loc.Userid

where a.EntryDate between @frdt and @todt and a.is_deleted<>1 
Group by name,Loc.Location  ) aa Left  Join (  select Days , User,  ifnull(Days,0)  *  60 * 60*8  Avail_Hrs_Sec , Location  from 
Temp_Working_Days  )bb on aa.Location=bb.Location and aa.User=bb.User
)aa  left join users bb on aa.User=bb.id 
-- Group by aa.Location
 Order by 1;
  


    
Drop table Temp_Working_Days; 

 
